insert into tb_movie(id, title, movie_year, description, img_url) values (1, 'American Fiction', 2023, 'Thelonious “Monk” Ellisons writing career has stalled because his work isnt deemed “Black enough.” Monk, a writer and English professor, writes a satirical novel under a pseudonym, aiming to expose the publishing worlds hypocrisies. The books immediate success forces him to get deeper enmeshed in his assumed identity and challenges his closely-held worldviews.', 'https://cinema10.com.br/upload/featuredImage.php?url=https%3A%2F%2Fcinema10.com.br%2Fupload%2Ffilmes%2Ffilmes_17249_american-fiction-poster.jpg')
