insert into tb_movie(title, movie_year, description, img_url) values ('American Fiction', 2023, 'Thelonious “Monk” Ellisons writing career has stalled because his work isnt deemed “Black enough.” Monk, a writer and English professor, writes a satirical novel under a pseudonym, aiming to expose the publishing worlds hypocrisies. The books immediate success forces him to get deeper enmeshed in his assumed identity and challenges his closely-held worldviews.', 'https://m.media-amazon.com/images/M/MV5BODVhYWI1YjMtNmM4NC00ZDhlLWJkOTgtMzE2ZGYyNjM3YzhjXkEyXkFqcGdeQXVyMTE0MzQwMjgz._V1_.jpg')
